import React, { useState } from 'react'
import { motion, AnimatePresence, Reorder } from 'framer-motion'
import { Plus, Trash2, GripVertical, Eye, EyeOff, Download, Upload, Copy, Check } from 'lucide-react'

type FieldType = 'text' | 'email' | 'number' | 'select' | 'textarea' | 'checkbox' | 'radio' | 'date'

interface FieldOption {
  id: string
  label: string
  value: string
}

interface FormField {
  id: string
  type: FieldType
  label: string
  placeholder?: string
  required: boolean
  options?: FieldOption[]
  validation?: {
    min?: number
    max?: number
    pattern?: string
  }
}

const FIELD_TYPES: { value: FieldType; label: string; icon: string }[] = [
  { value: 'text', label: 'Text Input', icon: 'Aa' },
  { value: 'email', label: 'Email', icon: '@' },
  { value: 'number', label: 'Number', icon: '123' },
  { value: 'select', label: 'Dropdown', icon: 'â–¼' },
  { value: 'textarea', label: 'Text Area', icon: 'Â¶' },
  { value: 'checkbox', label: 'Checkbox', icon: 'â˜‘' },
  { value: 'radio', label: 'Radio', icon: 'â—‰' },
  { value: 'date', label: 'Date', icon: 'ðŸ“…' }
]

export function DynamicFormBuilder() {
  const [fields, setFields] = useState<FormField[]>([
    {
      id: '1',
      type: 'text',
      label: 'Full Name',
      placeholder: 'Enter your full name',
      required: true
    },
    {
      id: '2',
      type: 'email',
      label: 'Email Address',
      placeholder: 'john@example.com',
      required: true
    }
  ])
  const [showPreview, setShowPreview] = useState(true)
  const [formName, setFormName] = useState('Contact Form')
  const [selectedField, setSelectedField] = useState<string | null>(null)
  const [copied, setCopied] = useState(false)

  const addField = (type: FieldType) => {
    const newField: FormField = {
      id: Date.now().toString(),
      type,
      label: `New ${type} field`,
      placeholder: '',
      required: false,
      options: type === 'select' || type === 'radio' ? [
        { id: '1', label: 'Option 1', value: 'option1' },
        { id: '2', label: 'Option 2', value: 'option2' }
      ] : undefined
    }
    setFields([...fields, newField])
    setSelectedField(newField.id)
  }

  const updateField = (id: string, updates: Partial<FormField>) => {
    setFields(fields.map(field => 
      field.id === id ? { ...field, ...updates } : field
    ))
  }

  const removeField = (id: string) => {
    setFields(fields.filter(field => field.id !== id))
    if (selectedField === id) setSelectedField(null)
  }

  const addOption = (fieldId: string) => {
    const field = fields.find(f => f.id === fieldId)
    if (field && field.options) {
      const newOption: FieldOption = {
        id: Date.now().toString(),
        label: `Option ${field.options.length + 1}`,
        value: `option${field.options.length + 1}`
      }
      updateField(fieldId, {
        options: [...field.options, newOption]
      })
    }
  }

  const updateOption = (fieldId: string, optionId: string, updates: Partial<FieldOption>) => {
    const field = fields.find(f => f.id === fieldId)
    if (field && field.options) {
      updateField(fieldId, {
        options: field.options.map(opt => 
          opt.id === optionId ? { ...opt, ...updates } : opt
        )
      })
    }
  }

  const removeOption = (fieldId: string, optionId: string) => {
    const field = fields.find(f => f.id === fieldId)
    if (field && field.options) {
      updateField(fieldId, {
        options: field.options.filter(opt => opt.id !== optionId)
      })
    }
  }

  const exportForm = () => {
    const formConfig = {
      name: formName,
      fields: fields
    }
    const blob = new Blob([JSON.stringify(formConfig, null, 2)], { type: 'application/json' })
    const url = URL.createObjectURL(blob)
