  const generateFormCode = () => {
    return `// ${formName} Component\nexport function ${formName.replace(/\s+/g, '')}Form() {\n  const [formData, setFormData] = useState({\n${fields.map(f => `    ${f.label.toLowerCase().replace(/\s+/g, '_')}: ''`).join(',\n')}\n  })\n\n  return (\n    <form className="space-y-4">\n      <h2 className="text-2xl font-bold">${formName}</h2>\n${fields.map(field => generateFieldCode(field)).join('\n')}\n      <button type="submit" className="px-6 py-3 bg-blue-600 text-white rounded-lg">\n        Submit\n      </button>\n    </form>\n  )\n}`
  }

  const generateFieldCode = (field: FormField): string => {
    const fieldName = field.label.toLowerCase().replace(/\s+/g, '_')
    switch (field.type) {
      case 'text':
      case 'email':
      case 'number':
      case 'date':
        return `      <div>\n        <label className="block text-sm font-medium mb-2">${field.label}</label>\n        <input\n          type="${field.type}"\n          placeholder="${field.placeholder || ''}"\n          required={${field.required}}\n          className="w-full px-4 py-2 border rounded-lg"\n        />\n      </div>`
      case 'textarea':
        return `      <div>\n        <label className="block text-sm font-medium mb-2">${field.label}</label>\n        <textarea\n          placeholder="${field.placeholder || ''}"\n          required={${field.required}}\n          rows={4}\n          className="w-full px-4 py-2 border rounded-lg"\n        />\n      </div>`
      case 'select':
        return `      <div>\n        <label className="block text-sm font-medium mb-2">${field.label}</label>\n        <select required={${field.required}} className="w-full px-4 py-2 border rounded-lg">\n          <option value="">Select an option</option>\n${field.options?.map(opt => `          <option value="${opt.value}">${opt.label}</option>`).join('\n')}\n        </select>\n      </div>`
      case 'checkbox':
        return `      <label className="flex items-center gap-2">\n        <input type="checkbox" className="rounded" />\n        <span>${field.label}</span>\n      </label>`
      case 'radio':
        return `      <div>\n        <label className="block text-sm font-medium mb-2">${field.label}</label>\n        <div className="space-y-2">\n${field.options?.map(opt => `          <label className="flex items-center gap-2">\n            <input type="radio" name="${fieldName}" value="${opt.value}" />\n            <span>${opt.label}</span>\n          </label>`).join('\n')}\n        </div>\n      </div>`
      default:
        return ''
    }
  }

  return (
