                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    className="
                      p-4 rounded-xl border-2 border-dashed border-gray-300
                      hover:border-blue-500 hover:bg-blue-50
                      transition-colors flex flex-col items-center gap-2
