import { ChevronDown, ChevronRight, Home, BarChart3, Users, Settings, Package, FileText, MessageSquare, Calendar, Shield, CreditCard, HelpCircle, Plus, Search, Bell, Menu, LogOut, Briefcase, Building2, Check } from 'lucide-react'

