    ]
  }
]

const bottomMenuItems: MenuItem[] = [
  {
    id: 'help',
    label: 'Help & Support',
    icon: <HelpCircle className="w-5 h-5" />,
    href: '/help'
  },
  {
    id: 'logout',
    label: 'Log Out',
    icon: <LogOut className="w-5 h-5" />,
    href: '/logout'
  }
]

export function VerticalSidebar() {
  const [isCollapsed, setIsCollapsed] = useState(false)
  const [expandedItems, setExpandedItems] = useState<Set<string>>(new Set(['analytics', 'team']))
  const [activeItem, setActiveItem] = useState('dashboard')
  const [showWorkspaceMenu, setShowWorkspaceMenu] = useState(false)
  const [selectedWorkspace, setSelectedWorkspace] = useState(workspaces[0])
  const [searchQuery, setSearchQuery] = useState('')
  const [showTooltip, setShowTooltip] = useState<string | null>(null)

  const sidebarRef = useRef<HTMLDivElement>(null)
  const workspaceMenuRef = useRef<HTMLDivElement>(null)

  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (workspaceMenuRef.current && !workspaceMenuRef.current.contains(event.target as Node)) {
        setShowWorkspaceMenu(false)
      }
    }

    document.addEventListener('mousedown', handleClickOutside)
    return () => document.removeEventListener('mousedown', handleClickOutside)
  }, [])

  const toggleExpanded = (itemId: string) => {
    const newExpanded = new Set(expandedItems)
    if (newExpanded.has(itemId)) {
      newExpanded.delete(itemId)
    } else {
      newExpanded.add(itemId)
    }
    setExpandedItems(newExpanded)
  }

  const handleItemClick = (itemId: string, hasChildren: boolean) => {
    if (hasChildren) {
      toggleExpanded(itemId)
    } else {
      setActiveItem(itemId)
    }
  }

  const filterMenuItems = (items: MenuItem[], query: string): MenuItem[] => {
    if (!query) return items

    return items.reduce((acc: MenuItem[], item) => {
      const matchesLabel = item.label.toLowerCase().includes(query.toLowerCase())
      const filteredChildren = item.children ? filterMenuItems(item.children, query) : []

      if (matchesLabel || filteredChildren.length > 0) {
        acc.push({
          ...item,
          children: filteredChildren.length > 0 ? filteredChildren : item.children
        })
      }

      return acc
    }, [])
  }

  const filteredMenuItems = filterMenuItems(menuItems, searchQuery)

  const renderMenuItem = (item: MenuItem, isNested = false) => {
    const isExpanded = expandedItems.has(item.id)
    const isActive = activeItem === item.id || item.children?.some(child => activeItem === child.id)
    const hasChildren = item.children && item.children.length > 0

    return (
      <div key={item.id}>
        <motion.div
          className="relative"
          onHoverStart={() => isCollapsed && !isNested && setShowTooltip(item.id)}
          onHoverEnd={() => setShowTooltip(null)}
        >
          <button
            onClick={() => handleItemClick(item.id, !!hasChildren)}
            disabled={item.disabled}
            className={`
              w-full flex items-center gap-3 px-3 py-2.5 rounded-lg transition-all duration-200
              ${isNested ? 'text-sm' : ''}
              ${isActive
                ? 'bg-blue-50 text-blue-600 font-medium'
                : 'text-gray-700 hover:bg-gray-100'
              }
              ${item.disabled ? 'opacity-50 cursor-not-allowed' : 'cursor-pointer'}
              ${isCollapsed && !isNested ? 'justify-center' : ''}
            `}
          >
            <span className={`flex-shrink-0 ${isNested ? 'ml-3' : ''}`}>
              {item.icon}
            </span>

            {!isCollapsed && (
              <>
                <span className="flex-1 text-left truncate">{item.label}</span>

                {item.badge && (
                  <span className={`
                    px-2 py-0.5 text-xs rounded-full flex-shrink-0
                    ${typeof item.badge === 'number' || !isNaN(Number(item.badge))
                      ? 'bg-red-100 text-red-600'
                      : 'bg-blue-100 text-blue-600'
                    }
                  `}>
                    {item.badge}
                  </span>
                )}

                {hasChildren && (
                  <motion.span
                    animate={{ rotate: isExpanded ? 180 : 0 }}
                    transition={{ duration: 0.2 }}
                  >
                    <ChevronDown className="w-4 h-4" />
                  </motion.span>
                )}
              </>
            )}

            {isCollapsed && item.badge && (
              <span className="absolute -top-1 -right-1 w-2 h-2 bg-red-500 rounded-full" />
            )}
          </button>

          {/* Tooltip for collapsed state */}
          <AnimatePresence>
            {isCollapsed && showTooltip === item.id && (
              <motion.div
                initial={{ opacity: 0, x: -10 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: -10 }}
                className="absolute left-full ml-2 top-1/2 -translate-y-1/2 z-50"
              >
                <div className="bg-gray-900 text-white text-sm px-3 py-2 rounded-lg whitespace-nowrap">
                  {item.label}
                  {item.badge && (
                    <span className="ml-2 px-2 py-0.5 text-xs bg-white/20 rounded-full">
                      {item.badge}
                    </span>
                  )}
                  <div className="absolute top-1/2 -left-1 -translate-y-1/2 w-2 h-2 bg-gray-900 rotate-45" />
                </div>
              </motion.div>
            )}
          </AnimatePresence>
        </motion.div>

        {/* Nested Items */}
        <AnimatePresence>
          {hasChildren && isExpanded && !isCollapsed && (
            <motion.div
              initial={{ height: 0, opacity: 0 }}
              animate={{ height: 'auto', opacity: 1 }}
              exit={{ height: 0, opacity: 0 }}
              transition={{ duration: 0.2 }}
              className="overflow-hidden"
            >
              <div className="ml-3 mt-1 space-y-1">
                {item.children.map(child => renderMenuItem(child, true))}
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    )
  }

  return (
    <div
      ref={sidebarRef}
      className={`
        h-screen bg-white border-r border-gray-200 flex flex-col transition-all duration-300
        ${isCollapsed ? 'w-20' : 'w-64'}
      `}
    >
      {/* Header */}
      <div className="p-4 border-b border-gray-200">
        <div className="flex items-center justify-between">
          {!isCollapsed ? (
            <div className="relative" ref={workspaceMenuRef}>
              <button
                onClick={() => setShowWorkspaceMenu(!showWorkspaceMenu)}
                className="w-full flex items-center gap-3 p-2 rounded-lg hover:bg-gray-100 transition-colors"
              >
                <div className="w-8 h-8 rounded-lg bg-gradient-to-br from-blue-500 to-purple-600 flex items-center justify-center text-white font-medium">
                  {selectedWorkspace.icon}
