      { id: 'timeline', label: 'Timeline', href: '/documents/projects/q4-planning/timeline' }
    ]
  },
  {
    id: 'reports',
    label: 'Reports',
    href: '/documents/projects/q4-planning/reports',
    icon: <Folder className="w-4 h-4" />,
