export function BreadcrumbNavigation({ 
  items = sampleBreadcrumbs, 
  maxItems = 4,
  onNavigate 
}: BreadcrumbProps) {
  const [activeDropdown, setActiveDropdown] = useState<string | null>(null)
  const [focusedIndex, setFocusedIndex] = useState(-1)
  const [isMobile, setIsMobile] = useState(false)
  const dropdownRefs = useRef<Record<string, HTMLDivElement>>({})
  const breadcrumbRefs = useRef<Record<string, HTMLButtonElement>>({})

  useEffect(() => {
    const checkMobile = () => {
      setIsMobile(window.innerWidth < 640)
    }
    checkMobile()
    window.addEventListener('resize', checkMobile)
    return () => window.removeEventListener('resize', checkMobile)
  }, [])

  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      const isClickInside = Object.values(dropdownRefs.current).some(ref => 
        ref && ref.contains(event.target as Node)
      )
      if (!isClickInside) {
        setActiveDropdown(null)
      }
    }

    document.addEventListener('mousedown', handleClickOutside)
    return () => document.removeEventListener('mousedown', handleClickOutside)
  }, [])

  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if (!items.length) return

      switch (e.key) {
        case 'ArrowLeft':
          e.preventDefault()
          setFocusedIndex(prev => Math.max(0, prev - 1))
          break
        case 'ArrowRight':
          e.preventDefault()
          setFocusedIndex(prev => Math.min(items.length - 1, prev + 1))
          break
        case 'Enter':
        case ' ':
          if (focusedIndex >= 0 && focusedIndex < items.length) {
            e.preventDefault()
            const item = getVisibleItems()[focusedIndex]
            if (item) {
              handleItemClick(item)
            }
          }
          break
        case 'Escape':
          setActiveDropdown(null)
          setFocusedIndex(-1)
          break
      }
    }

    window.addEventListener('keydown', handleKeyDown)
    return () => window.removeEventListener('keydown', handleKeyDown)
  }, [focusedIndex, items])

  useEffect(() => {
    if (focusedIndex >= 0) {
      const visibleItems = getVisibleItems()
      const item = visibleItems[focusedIndex]
      if (item && breadcrumbRefs.current[item.id]) {
        breadcrumbRefs.current[item.id].focus()
      }
    }
  }, [focusedIndex])

  const getVisibleItems = () => {
    if (isMobile && items.length > 2) {
      return [items[0], { id: 'ellipsis', label: '...', href: '#' }, items[items.length - 1]]
    }

    if (items.length <= maxItems) {
      return items
    }

    const firstItem = items[0]
    const lastItems = items.slice(-(maxItems - 2))
    return [firstItem, { id: 'ellipsis', label: '...', href: '#' }, ...lastItems]
  }

  const getCollapsedItems = () => {
    if (isMobile && items.length > 2) {
      return items.slice(1, -1)
    }

    if (items.length <= maxItems) {
      return []
    }

    return items.slice(1, -(maxItems - 2))
  }

  const handleItemClick = (item: BreadcrumbItem) => {
    if (item.id === 'ellipsis') {
      setActiveDropdown('ellipsis')
    } else if (item.children && item.children.length > 0) {
      setActiveDropdown(activeDropdown === item.id ? null : item.id)
    } else {
      if (onNavigate) {
        onNavigate(item)
      } else {
        console.log('Navigate to:', item.href)
      }
      setActiveDropdown(null)
    }
  }

  const renderBreadcrumbItem = (item: BreadcrumbItem, index: number, isLast: boolean) => {
    const isActive = activeDropdown === item.id
    const isEllipsis = item.id === 'ellipsis'
    const collapsedItems = getCollapsedItems()

    return (
      <React.Fragment key={item.id}>
        <li className="flex items-center">
          <div className="relative" ref={el => { if (el) dropdownRefs.current[item.id] = el }}>
            <motion.button
              ref={el => { if (el) breadcrumbRefs.current[item.id] = el }}
              onClick={() => handleItemClick(item)}
              onFocus={() => setFocusedIndex(index)}
              onBlur={() => setFocusedIndex(-1)}
              className={`
                flex items-center gap-2 px-3 py-2 rounded-lg text-sm font-medium transition-all
                ${isLast && !isEllipsis
                  ? 'text-gray-900 bg-gray-100 cursor-default'
                  : 'text-gray-600 hover:text-gray-900 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-blue-500'
                }
                ${focusedIndex === index ? 'ring-2 ring-blue-500' : ''}
              `}
              whileHover={!isLast ? { scale: 1.02 } : {}}
              whileTap={!isLast ? { scale: 0.98 } : {}}
            >
              {item.icon && <span className="flex-shrink-0">{item.icon}</span>}
              <span className={`${isMobile && !isEllipsis ? 'max-w-[100px] truncate' : ''}`}>
                {isEllipsis ? <MoreHorizontal className="w-4 h-4" /> : item.label}
              </span>
              {item.children && item.children.length > 0 && !isEllipsis && (
                <ChevronDown className={`w-3 h-3 transition-transform ${isActive ? 'rotate-180' : ''}`} />
              )}
              {item.metadata?.starred && (
                <Star className="w-3 h-3 fill-yellow-400 text-yellow-400" />
              )}
            </motion.button>

            {/* Dropdown Menu */}
            <AnimatePresence>
              {isActive && (item.children || isEllipsis) && (
                <motion.div
                  initial={{ opacity: 0, y: -10 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -10 }}
                  transition={{ duration: 0.2 }}
                  className="absolute top-full left-0 mt-2 w-64 bg-white rounded-xl shadow-lg border border-gray-200 overflow-hidden z-50"
                >
                  <div className="max-h-80 overflow-y-auto">
                    {isEllipsis ? (
                      <div className="py-2">
                        {collapsedItems.map((collapsedItem, idx) => (
                          <button
                            key={collapsedItem.id}
                            onClick={() => {
                              handleItemClick(collapsedItem)
                              setActiveDropdown(null)
                            }}
                            className="w-full flex items-center gap-3 px-4 py-2.5 text-left hover:bg-gray-50 transition-colors"
                          >
                            <span className="flex-shrink-0 text-gray-400">
                              {collapsedItem.icon || <Folder className="w-4 h-4" />}
                            </span>
                            <div className="flex-1 min-w-0">
                              <p className="text-sm font-medium text-gray-900 truncate">
                                {collapsedItem.label}
                              </p>
                              <p className="text-xs text-gray-500">in {items[idx].label}</p>
                            </div>
                            <ChevronRight className="w-4 h-4 text-gray-400 flex-shrink-0" />
                          </button>
                        ))}
                      </div>
                    ) : (
                      <div className="py-2">
                        <div className="px-4 py-2 border-b border-gray-100">
                          <p className="text-xs font-medium text-gray-500 uppercase">Navigate to</p>
                        </div>
                        {item.children?.map((child) => (
                          <button
                            key={child.id}
                            onClick={() => {
                              if (onNavigate) {
                                onNavigate(child)
                              } else {
                                console.log('Navigate to:', child.href)
                              }
                              setActiveDropdown(null)
                            }}
                            className="w-full flex items-center gap-3 px-4 py-2.5 text-left hover:bg-gray-50 transition-colors"
                          >
                            <span className="flex-shrink-0 text-gray-400">
                              {child.icon || <Folder className="w-4 h-4" />}
                            </span>
                            <span className="text-sm font-medium text-gray-900">
                              {child.label}
                            </span>
                          </button>
                        ))}
                      </div>
                    )}
                  </div>
                  {item.metadata && (
                    <div className="px-4 py-3 bg-gray-50 border-t border-gray-100">
                      {item.metadata.lastModified && (
                        <p className="text-xs text-gray-500 flex items-center gap-1">
                          <Clock className="w-3 h-3" />
                          Modified {item.metadata.lastModified}
                        </p>
                      )}
                      {item.metadata.description && (
                        <p className="text-xs text-gray-600 mt-1">
                          {item.metadata.description}
                        </p>
                      )}
                    </div>
                  )}
                </motion.div>
              )}
            </AnimatePresence>
          </div>
        </li>

        {!isLast && (
          <li className="flex items-center px-2">
            <ChevronRight className="w-4 h-4 text-gray-400" />
          </li>
        )}
      </React.Fragment>
    )
  }

  const visibleItems = getVisibleItems()

  return (
    <nav aria-label="Breadcrumb" className="w-full">
      <motion.ol 
        className="flex items-center flex-wrap"
        initial={{ opacity: 0, y: -10 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.3 }}
      >
        {visibleItems.map((item, index) => 
          renderBreadcrumbItem(item, index, index === visibleItems.length - 1)
        )}
      </motion.ol>

      {/* Mobile Indicator */}
      {isMobile && items.length > 2 && (
        <motion.p 
          className="text-xs text-gray-500 mt-2 ml-3"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.3 }}
        >
          {items.length - 2} more items
        </motion.p>
      )}
    </nav>
  )
}

