import React, { useState, useMemo } from 'react'
import { motion, AnimatePresence } from 'framer-motion'
import { ChevronUp, ChevronDown, Search, Filter, Download, Trash2, Edit, MoreHorizontal, ChevronLeft, ChevronRight, Eye, EyeOff, Check, X } from 'lucide-react'

