]

export function KanbanBoard() {
  const [boardState, setBoardState] = useState<BoardState>(initialData)
  const [showNewTaskModal, setShowNewTaskModal] = useState(false)
  const [selectedColumn, setSelectedColumn] = useState<string>('')
  const [editingColumn, setEditingColumn] = useState<string | null>(null)
  const [newColumnTitle, setNewColumnTitle] = useState('')
  const [showRealtimeIndicator, setShowRealtimeIndicator] = useState(false)

  // Simulate real-time updates
  useEffect(() => {
    const interval = setInterval(() => {
      setShowRealtimeIndicator(true)
      setTimeout(() => setShowRealtimeIndicator(false), 2000)
    }, 30000) // Every 30 seconds

    return () => clearInterval(interval)
  }, [])

  const handleDragEnd = (result: DropResult) => {
    const { source, destination } = result

    if (!destination) return

    const sourceColIndex = boardState.columns.findIndex(col => col.id === source.droppableId)
    const destColIndex = boardState.columns.findIndex(col => col.id === destination.droppableId)

    const sourceCol = boardState.columns[sourceColIndex]
    const destCol = boardState.columns[destColIndex]

    const sourceTasks = [...sourceCol.tasks]
    const [movedTask] = sourceTasks.splice(source.index, 1)

    if (source.droppableId === destination.droppableId) {
      // Moving within the same column
      sourceTasks.splice(destination.index, 0, movedTask)
      const newColumns = [...boardState.columns]
      newColumns[sourceColIndex] = { ...sourceCol, tasks: sourceTasks }
      setBoardState({ ...boardState, columns: newColumns, lastUpdate: new Date().toISOString() })
    } else {
      // Moving to different column
      const destTasks = [...destCol.tasks]
      destTasks.splice(destination.index, 0, movedTask)

      const newColumns = [...boardState.columns]
      newColumns[sourceColIndex] = { ...sourceCol, tasks: sourceTasks }
      newColumns[destColIndex] = { ...destCol, tasks: destTasks }
      setBoardState({ ...boardState, columns: newColumns, lastUpdate: new Date().toISOString() })
    }
  }

  const addNewTask = (columnId: string, task: Omit<Task, 'id' | 'createdAt'>) => {
    const newTask: Task = {
      ...task,
      id: `task-${Date.now()}`,
      createdAt: new Date().toISOString()
    }

    const columnIndex = boardState.columns.findIndex(col => col.id === columnId)
    const column = boardState.columns[columnIndex]
    const newColumns = [...boardState.columns]
    newColumns[columnIndex] = { ...column, tasks: [...column.tasks, newTask] }

    setBoardState({ ...boardState, columns: newColumns, lastUpdate: new Date().toISOString() })
    setShowNewTaskModal(false)
  }

  const deleteTask = (columnId: string, taskId: string) => {
    const columnIndex = boardState.columns.findIndex(col => col.id === columnId)
    const column = boardState.columns[columnIndex]
    const newTasks = column.tasks.filter(task => task.id !== taskId)
    const newColumns = [...boardState.columns]
    newColumns[columnIndex] = { ...column, tasks: newTasks }

    setBoardState({ ...boardState, columns: newColumns, lastUpdate: new Date().toISOString() })
  }

  const updateColumnTitle = (columnId: string, newTitle: string) => {
    const columnIndex = boardState.columns.findIndex(col => col.id === columnId)
    const newColumns = [...boardState.columns]
    newColumns[columnIndex] = { ...newColumns[columnIndex], title: newTitle }
    setBoardState({ ...boardState, columns: newColumns })
    setEditingColumn(null)
    setNewColumnTitle('')
  }

  const addNewColumn = () => {
    const newColumn: Column = {
      id: `col-${Date.now()}`,
      title: 'New Column',
      color: '#9CA3AF',
      tasks: []
    }
    setBoardState({ ...boardState, columns: [...boardState.columns, newColumn] })
  }

  const getPriorityIcon = (priority: string) => {
    return <Flag className="w-3 h-3" />
  }

  const formatDate = (date: string) => {
    return new Date(date).toLocaleDateString('en-US', { month: 'short', day: 'numeric' })
  }

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      {/* Header */}
      <div className="mb-8">
        <div className="flex items-center justify-between">
          <div>
