            <p className="text-gray-600 mt-1">Manage your tasks with drag and drop</p>
          </div>
          <div className="flex items-center gap-4">
            {/* Real-time indicator */}
            <AnimatePresence>
              {showRealtimeIndicator && (
                <motion.div
                  initial={{ opacity: 0, scale: 0.8 }}
                  animate={{ opacity: 1, scale: 1 }}
                  exit={{ opacity: 0, scale: 0.8 }}
                  className="flex items-center gap-2 px-3 py-1.5 bg-green-100 text-green-700 rounded-full text-sm"
                >
                  <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse" />
                  Board synced
                </motion.div>
              )}
            </AnimatePresence>

            <span className="text-sm text-gray-500">
              Last updated: {new Date(boardState.lastUpdate).toLocaleTimeString()}
            </span>

            <button
              onClick={addNewColumn}
              className="px-4 py-2 bg-gray-200 text-gray-700 rounded-lg hover:bg-gray-300 transition-colors flex items-center gap-2"
            >
              <Plus className="w-5 h-5" />
              Add Column
            </button>
          </div>
        </div>
      </div>

      {/* Kanban Board */}
      <DragDropContext onDragEnd={handleDragEnd}>
        <div className="flex gap-6 overflow-x-auto pb-6">
          {boardState.columns.map((column) => (
            <div key={column.id} className="flex-shrink-0 w-80">
              <div className="bg-white rounded-xl shadow-sm border border-gray-200">
                {/* Column Header */}
                <div className="p-4 border-b border-gray-200">
                  <div className="flex items-center justify-between mb-2">
                    {editingColumn === column.id ? (
                      <div className="flex items-center gap-2 flex-1">
                        <input
                          type="text"
                          value={newColumnTitle || column.title}
                          onChange={(e) => setNewColumnTitle(e.target.value)}
                          onKeyPress={(e) => {
                            if (e.key === 'Enter') {
                              updateColumnTitle(column.id, newColumnTitle || column.title)
                            }
                          }}
                          className="flex-1 px-2 py-1 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                          autoFocus
                        />
                        <button
