```tsx
import React, { useState, useRef, useEffect, KeyboardEvent } from 'react'
import { motion, AnimatePresence } from 'framer-motion'
import { Save, X, Check, AlertCircle, Download, Upload, Copy, Trash2, Plus, Edit2, FileSpreadsheet } from 'lucide-react'

interface CellData {
  value: string | number | boolean
  type: 'text' | 'number' | 'email' | 'date' | 'select' | 'boolean'
  options?: string[]
  validation?: {
    required?: boolean
    min?: number
    max?: number
    pattern?: RegExp
    custom?: (value: any) => string | null
  }
}

interface RowData {
  id: string
  cells: Record<string, CellData>
}

interface Column {
  key: string
  header: string
  width?: number
  type: CellData['type']
  editable?: boolean
  options?: string[]
  validation?: CellData['validation']
}

interface EditingCell {
  rowId: string
  columnKey: string
  value: any
}

const initialColumns: Column[] = [
  { key: 'name', header: 'Name', type: 'text', editable: true, validation: { required: true } },
  { key: 'email', header: 'Email', type: 'email', editable: true, validation: { required: true, pattern: /^[^\s@]+@[^\s@]+\.[^\s@]+$/ } },
  { key: 'age', header: 'Age', type: 'number', width: 100, editable: true, validation: { min: 18, max: 100 } },
  { key: 'department', header: 'Department', type: 'select', editable: true, options: ['Engineering', 'Design', 'Marketing', 'Sales', 'HR'] },
  { key: 'salary', header: 'Salary', type: 'number', editable: true, validation: { min: 0 } },
  { key: 'active', header: 'Active', type: 'boolean', width: 100, editable: true },
  { key: 'startDate', header: 'Start Date', type: 'date', editable: true }
]

const initialRows: RowData[] = [
  {
    id: '1',
    cells: {
      name: { value: 'John Doe', type: 'text' },
      email: { value: 'john.doe@example.com', type: 'email' },
      age: { value: 32, type: 'number' },
      department: { value: 'Engineering', type: 'select' },
      salary: { value: 85000, type: 'number' },
      active: { value: true, type: 'boolean' },
      startDate: { value: '2022-03-15', type: 'date' }
    }
  },
  {
    id: '2',
    cells: {
      name: { value: 'Jane Smith', type: 'text' },
      email: { value: 'jane.smith@example.com', type: 'email' },
      age: { value: 28, type: 'number' },
      department: { value: 'Design', type: 'select' },
      salary: { value: 75000, type: 'number' },
      active: { value: true, type: 'boolean' },
      startDate: { value: '2023-01-10', type: 'date' }
    }
  },
  {
    id: '3',
    cells: {
      name: { value: 'Bob Johnson', type: 'text' },
      email: { value: 'bob.j@example.com', type: 'email' },
      age: { value: 45, type: 'number' },
      department: { value: 'Sales', type: 'select' },
      salary: { value: 92000, type: 'number' },
      active: { value: false, type: 'boolean' },
      startDate: { value: '2021-07-22', type: 'date' }
    }
  }
]

export function DataGrid() {
  const [columns] = useState(initialColumns)
  const [rows, setRows] = useState(initialRows)
  const [editingCell, setEditingCell] = useState<EditingCell | null>(null)
  const [selectedCells, setSelectedCells] = useState<Set<string>>(new Set())
  const [errors, setErrors] = useState<Record<string, string>>({})
  const [focusedCell, setFocusedCell] = useState<{ rowId: string; columnKey: string } | null>(null)
  const [copiedData, setCopiedData] = useState<any>(null)
  const [undoStack, setUndoStack] = useState<RowData[][]>([])
  const [redoStack, setRedoStack] = useState<RowData[][]>([])

  const gridRef = useRef<HTMLDivElement>(null)
  const inputRefs = useRef<Record<string, HTMLInputElement | HTMLSelectElement>>({})

  // Keyboard navigation
  useEffect(() => {
    const handleKeyDown = (e: globalThis.KeyboardEvent) => {
      if (!focusedCell) return

      const { rowId, columnKey } = focusedCell
      const rowIndex = rows.findIndex(r => r.id === rowId)
      const colIndex = columns.findIndex(c => c.key === columnKey)

      switch (e.key) {
        case 'ArrowUp':
          e.preventDefault()
          if (rowIndex > 0) {
            setFocusedCell({ rowId: rows[rowIndex - 1].id, columnKey })
          }
          break
        case 'ArrowDown':
          e.preventDefault()
          if (rowIndex < rows.length - 1) {
            setFocusedCell({ rowId: rows[rowIndex + 1].id, columnKey })
          }
          break
        case 'ArrowLeft':
          e.preventDefault()
          if (colIndex > 0) {
            setFocusedCell({ rowId, columnKey: columns[colIndex - 1].key })
          }
          break
        case 'ArrowRight':
          e.preventDefault()
          if (colIndex < columns.length - 1) {
            setFocusedCell({ rowId, columnKey: columns[colIndex + 1].key })
          }
          break
        case 'Enter':
          e.preventDefault()
          if (!editingCell && columns[colIndex].editable) {
            startEditing(rowId, columnKey)
          }
          break
        case 'Escape':
          if (editingCell) {
            cancelEditing()
          }
          break
        case 'Delete':
        case 'Backspace':
          if (!editingCell && columns[colIndex].editable) {
            updateCell(rowId, columnKey, '')
          }
          break
      }

      // Copy/Paste shortcuts
      if (e.ctrlKey || e.metaKey) {
        switch (e.key) {
          case 'c':
            e.preventDefault()
            copyCell()
            break
          case 'v':
            e.preventDefault()
            pasteCell()
            break
          case 'z':
            e.preventDefault()
            undo()
            break
          case 'y':
            e.preventDefault()
            redo()
            break
        }
      }
    }

    window.addEventListener('keydown', handleKeyDown)
    return () => window.removeEventListener('keydown', handleKeyDown)
  }, [focusedCell, editingCell, rows, columns])

  const validateCell = (value: any, column: Column): string | null => {
    const validation = column.validation
    if (!validation) return null

    if (validation.required && !value) {
      return 'This field is required'
    }

    if (column.type === 'number') {
      const numValue = Number(value)
      if (validation.min !== undefined && numValue < validation.min) {
        return `Value must be at least ${validation.min}`
      }
      if (validation.max !== undefined && numValue > validation.max) {
        return `Value must be at most ${validation.max}`
      }
    }

    if (column.type === 'email' && validation.pattern) {
      if (!validation.pattern.test(value)) {
        return 'Invalid email format'
      }
    }

    if (validation.custom) {
      return validation.custom(value)
    }

    return null
  }

  const startEditing = (rowId: string, columnKey: string) => {
    const row = rows.find(r => r.id === rowId)
    if (!row) return

    setEditingCell({
      rowId,
      columnKey,
      value: row.cells[columnKey]?.value || ''
    })

    // Focus input after render
    setTimeout(() => {
      const input = inputRefs.current[`${rowId}-${columnKey}`]
      if (input) {
        input.focus()
        if ('select' in input) {
          input.select()
        }
      }
    }, 0)
  }

  const saveEdit = () => {
    if (!editingCell) return

    const column = columns.find(c => c.key === editingCell.columnKey)
    if (!column) return

    const error = validateCell(editingCell.value, column)
    if (error) {
      setErrors({ [`${editingCell.rowId}-${editingCell.columnKey}`]: error })
      return
    }

    updateCell(editingCell.rowId, editingCell.columnKey, editingCell.value)
    setEditingCell(null)
    setErrors({})
  }

  const cancelEditing = () => {
    setEditingCell(null)
    setErrors({})
  }

  const updateCell = (rowId: string, columnKey: string, value: any) => {
    // Save current state for undo
    setUndoStack([...undoStack, rows])
    setRedoStack([])

    setRows(rows.map(row => {
      if (row.id === rowId) {
        return {
          ...row,
          cells: {
            ...row.cells,
            [columnKey]: {
              ...row.cells[columnKey],
              value
            }
          }
        }
      }
      return row
    }))
  }

  const addRow = () => {
    const newRow: RowData = {
      id: `row-${Date.now()}`,
      cells: columns.reduce((acc, col) => {
        acc[col.key] = {
          value: col.type === 'boolean' ? false : '',
          type: col.type,
          options: col.options
        }
        return acc
      }, {} as Record<string, CellData>)
    }

    setUndoStack([...undoStack, rows])
    setRedoStack([])
    setRows([...rows, newRow])
  }

  const deleteRow = (rowId: string) => {
    setUndoStack([...undoStack, rows])
    setRedoStack([])
    setRows(rows.filter(r => r.id !== rowId))
  }

  const copyCell = () => {
    if (!focusedCell) return
    const row = rows.find(r => r.id === focusedCell.rowId)
    if (!row) return

    const value = row.cells[focusedCell.columnKey]?.value
    setCopiedData(value)

    // Visual feedback
    const cellKey = `${focusedCell.rowId}-${focusedCell.columnKey}`
    setSelectedCells(new Set([cellKey]))
    setTimeout(() => setSelectedCells(new Set()), 200)
  }

  const pasteCell = () => {
    if (!focusedCell || copiedData === null) return
    const column = columns.find(c => c.key === focusedCell.columnKey)
    if (!column?.editable) return

    updateCell(focusedCell.rowId, focusedCell.columnKey, copiedData)
  }

  const undo = () => {
    if (undoStack.length === 0) return
    const previousState = undoStack[undoStack.length - 1]
    setRedoStack([...redoStack, rows])
    setUndoStack(undoStack.slice(0, -1))
    setRows(previousState)
  }

  const redo = () => {
    if (redoStack.length === 0) return
    const nextState = redoStack[redoStack.length - 1]
    setUndoStack([...undoStack, rows])
    setRedoStack(redoStack.slice(0, -1))
    setRows(nextState)
  }

  const exportToCSV = () => {
    const headers = columns.map(col => col.header).join(',')
    const csvRows = rows.map(row => 
      columns.map(col => {
        const value = row.cells[col.key]?.value || ''
        // Escape quotes and wrap in quotes if contains comma
        const stringValue = String(value)
        return stringValue.includes(',') ? `"${stringValue.replace(/"/g, '""')}"` : stringValue
      }).join(',')
    )

    const csv = [headers, ...csvRows].join('\n')
    const blob = new Blob([csv], { type: 'text/csv' })
    const url = URL.createObjectURL(blob)
    const a = document.createElement('a')
    a.href = url
    a.download = `data-export-${new Date().toISOString().split('T')[0]}.csv`
    a.click()
    URL.revokeObjectURL(url)
  }

  const exportToJSON = () => {
    const jsonData = rows.map(row => {
      const rowData: Record<string, any> = { id: row.id }
      columns.forEach(col => {
        rowData[col.key] = row.cells[col.key]?.value || null
      })
      return rowData
    })

    const json = JSON.stringify(jsonData, null, 2)
    const blob = new Blob([json], { type: 'application/json' })
    const url = URL.createObjectURL(blob)
    const a = document.createElement('a')
    a.href = url
    a.download = `data-export-${new Date().toISOString().split('T')[0]}.json`
    a.click()
    URL.revokeObjectURL(url)
  }

  const renderCell = (row: RowData, column: Column) => {
    const cellKey = `${row.id}-${column.key}`
    const isEditing = editingCell?.rowId === row.id && editingCell?.columnKey === column.key
    const isFocused = focusedCell?.rowId === row.id && focusedCell?.columnKey === column.key
    const isSelected = selectedCells.has(cellKey)
    const hasError = errors[cellKey]
    const cellValue = row.cells[column.key]?.value

    if (isEditing) {
      return (
        <div className="relative">
          {column.type === 'select' ? (
            <select
              ref={(el) => { if (el) inputRefs.current[cellKey] = el }}
              value={editingCell.value}
              onChange={(e) => setEditingCell({ ...editingCell, value: e.target.value })}
              onBlur={saveEdit}
              onKeyDown={(e) => {
                if (e.key === 'Enter') saveEdit()
                if (e.key === 'Escape') cancelEditing()
              }}
              className="w-full px-2 py-1 border border-blue-500 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="">Select...</option>
              {column.options?.map(opt => (
                <option key={opt} value={opt}>{opt}</option>
              ))}
            </select>
          ) : column.type === 'boolean' ? (
            <input
              type="checkbox"
              checked={editingCell.value}
              onChange={(e) => {
                setEditingCell({ ...editingCell, value: e.target.checked })
                updateCell(row.id, column.key, e.target.checked)
                setEditingCell(null)
              }}
              className="w-4 h-4 text-blue-600 rounded"
            />
          ) : (
            <input
              ref={(el) => { if (el) inputRefs.current[cellKey] = el }}
              type={column.type === 'number' ? 'number' : column.type === 'date' ? 'date' : 'text'}
              value={editingCell.value}
              onChange={(e) => setEditingCell({ ...editingCell, value: e.target.value })}
              onBlur={saveEdit}
              onKeyDown={(e) => {
                if (e.key === 'Enter') saveEdit()
                if (e.key === 'Escape') cancelEditing()
              }}
              className="w-full px-2 py-1 border border-blue-500 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          )}
          {hasError && (
            <div className="absolute top-full left-0 mt-1 p-2 bg-red-50 border border-red-200 rounded text-xs text-red-600 whitespace-nowrap z-10 flex items-center gap-1">
              <AlertCircle className="w-3 h-3" />
              {hasError}
            </div>
          )}
        </div>
      )
    }

    return (
      <div
        className={`px-3 py-2 cursor-pointer transition-all ${
          isFocused ? 'ring-2 ring-blue-500 ring-inset' : ''
        } ${isSelected ? 'bg-blue-50' : ''} ${column.editable ? 'hover:bg-gray-50' : ''}`}
        onClick={() => {
          setFocusedCell({ rowId: row.id, columnKey: column.key })
          if (column.editable) {
            startEditing(row.id, column.key)
          }
        }}
      >
        {column.type === 'boolean' ? (
          <input
            type="checkbox"
            checked={cellValue || false}
            onChange={(e) => {
              if (column.editable) {
                updateCell(row.id, column.key, e.target.checked)
              }
            }}
            onClick={(e) => e.stopPropagation()}
            disabled={!column.editable}
            className="w-4 h-4 text-blue-600 rounded disabled:opacity-50"
          />
        ) : column.type === 'number' && typeof cellValue === 'number' ? (
          <span>{column.key === 'salary' ? `$${cellValue.toLocaleString()}` : cellValue}</span>
        ) : (
          <span>{cellValue || '-'}</span>
        )}
      </div>
    )
  }

  return (
    <div className="w-full bg-white rounded-xl shadow-lg border border-gray-200">
      {/* Toolbar */}
      <div className="p-4 border-b border-gray-200">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-4">
            <h2 className="text-xl font-semibold text-gray-900">Data Grid</h2>
            <div className="flex items-center gap-2 text-sm text-gray-600">
              <span>{rows.length} rows</span>
              <span></span>
              <span>{columns.length} columns</span>
            </div>
          </div>

          <div className="flex items-center gap-2">
            {/* Undo/Redo */}
            <button
              onClick={undo}
              disabled={undoStack.length === 0}
              className="p-2 text-gray-600 hover:bg-gray-100 rounded-lg disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
              title="Undo (Ctrl+Z)"
            >
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 10h10a8 8 0 018 8v2M3 10l6 6m-6-6l6-6" />
              </svg>
            </button>
            <button
              onClick={redo}
              disabled={redoStack.length === 0}
              className="p-2 text-gray-600 hover:bg-gray-100 rounded-lg disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
              title="Redo (Ctrl+Y)"
            >
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 10h-10a8 8 0 00-8 8v2M21 10l-6 6m6-6l-6-6" />
              </svg>
            </button>

            <div className="w-px h-6 bg-gray-300" />

            {/* Actions */}
            <button
              onClick={addRow}
              className="px-3 py-1.5 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors flex items-center gap-2"
            >
              <Plus className="w-4 h-4" />
              Add Row
            </button>

            <div className="relative group">
              <button className="p-2 text-gray-600 hover:bg-gray-100 rounded-lg transition-colors">
                <Download className="w-5 h-5" />
              </button>
              <div className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg border border-gray-200 opacity-0 invisible group-hover:opacity-100 group-hover:visible transition-all z-10">
                <button
                  onClick={exportToCSV}
                  className="w-full text-left px-4 py-2 hover:bg-gray-50 transition-colors flex items-center gap-2"
                >
                  <FileSpreadsheet className="w-4 h-4" />
                  Export as CSV
                </button>
                <button
                  onClick={exportToJSON}
                  className="w-full text-left px-4 py-2 hover:bg-gray-50 transition-colors flex items-center gap-2"
                >
                  <FileSpreadsheet className="w-4 h-4" />
                  Export as JSON
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Grid */}
      <div ref={gridRef} className="overflow-auto max-h-[600px]">
        <table className="w-full">
          <thead className="sticky top-0 bg-gray-50 border-b border-gray-200">
            <tr>
              <th className="w-12 px-4 py-3 text-left">#</th>
              {columns.map((column) => (
                <th
                  key={column.key}
                  className="px-3 py-3 text-left font-medium text-gray-900"
                  style={{ width: column.width }}
                >
                  <div className="flex items-center gap-2">
                    {column.header}
                    {column.editable && (
                      <Edit2 className="w-3 h-3 text-gray-400" />
                    )}
                  </div>
                </th>
              ))}
              <th className="w-20 px-4 py-3">Actions</th>
            </tr>
          </thead>
          <tbody>
            <AnimatePresence>
              {rows.map((row, index) => (
                <motion.tr
                  key={row.id}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, x: -20 }}
                  transition={{ delay: index * 0.05 }}
                  className="border-b border-gray-100 hover:bg-gray-50 transition-colors"
                >
                  <td className="px-4 py-2 text-sm text-gray-500">{index + 1}</td>
                  {columns.map((column) => (
                    <td key={column.key} className="relative">
                      {renderCell(row, column)}
                    </td>
                  ))}
                  <td className="px-4 py-2">
                    <button
                      onClick={() => deleteRow(row.id)}
                      className="p-1.5 text-red-600 hover:bg-red-50 rounded transition-colors"
                    >
                      <Trash2 className="w-4 h-4" />
                    </button>
                  </td>
                </motion.tr>
              ))}
            </AnimatePresence>
          </tbody>
        </table>
      </div>

      {/* Footer */}
      <div className="p-4 border-t border-gray-200 bg-gray-50">
        <div className="flex items-center justify-between text-sm text-gray-600">
          <div className="flex items-center gap-4">
            <span>Keyboard shortcuts:</span>
            <kbd className="px-2 py-1 bg-gray-200 rounded text-xs"></kbd>
            <span>Navigate</span>
            <kbd className="px-2 py-1 bg-gray-200 rounded text-xs">Enter</kbd>
            <span>Edit</span>
            <kbd className="px-2 py-1 bg-gray-200 rounded text-xs">Esc</kbd>
            <span>Cancel</span>
            <kbd className="px-2 py-1 bg-gray-200 rounded text-xs">Ctrl+C/V</kbd>
            <span>Copy/Paste</span>
          </div>
          {copiedData !== null && (
            <motion.div
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              className="flex items-center gap-2 text-green-600"
            >
              <Check className="w-4 h-4" />
              Value copied
            </motion.div>
          )}
        </div>
      </div>
    </div>
  )
}
